    }

    OnKeyUp(*) {
        if this.pressTime {
            elapsed := A_TickCount - this.pressTime
            this.pressTime := 0

            if elapsed >= this.holdThreshold
                this.holdAction.Call()
            else
                this.tapAction.Call()
        }
    }
