class FileWatcher {
    __New(directory, callback, filter := '*.*') {
        this.directory := directory
        this.callback := callback
        this.filter := filter
        this.fileStates := Map()
        this.running := false
    }

    Start(interval := 1000) {
        this.running := true
        this.ScanDirectory()
        SetTimer(this.CheckChanges.Bind(this), interval)
    }

    Stop() {
        this.running := false
        SetTimer(this.CheckChanges.Bind(this), 0)
    }

    ScanDirectory() {
        loop files this.directory '\' this.filter, 'F' {
            this.fileStates[A_LoopFilePath] := FileGetTime(A_LoopFilePath)
        }
    }

    CheckChanges() {
        currentFiles := Map()

        loop files this.directory '\' this.filter, 'F' {
            path := A_LoopFilePath
            modTime := FileGetTime(path)
            currentFiles[path] := modTime

            if !this.fileStates.Has(path)
                this.callback.Call('created', path)
            else if this.fileStates[path] != modTime
                this.callback.Call('modified', path)
        }

        for path, time in this.fileStates {
            if !currentFiles.Has(path)
                this.callback.Call('deleted', path)
        }

        this.fileStates := currentFiles
    }
}

