        IniWrite(value, this.iniPath, section, key)
        return this
    }

    GetSection(section) {
        result := Map()
        content := IniRead(this.iniPath, section, '')

        for line in StrSplit(content, '`n') {
            if InStr(line, '=') {
                parts := StrSplit(line, '=', , 2)
                result[parts[1]] := parts[2]
            }
        }

        return result
    }

    InitDefaults() {
        for section, keys in this.defaults {
            for key, value in keys {
                if this.Get(section, key) = ''
                    this.Set(section, key, value)
            }
        }
    }
