class StringUtils {
    static ToCamelCase(str) {
        result := ''
        capitalize := false

        for char in StrSplit(str) {
            if char = ' ' || char = '_' || char = '-' {
                capitalize := true
                continue
            }
            result .= capitalize ? StrUpper(char) : StrLower(char)
            capitalize := false
        }

        return result
    }

    static ToSnakeCase(str) {
        result := ''

        for i, char in StrSplit(str) {
            if RegExMatch(char, '[A-Z]') && i > 1
                result .= '_'
            result .= StrLower(char)
        }

        return StrReplace(result, ' ', '_')
    }

    static ToKebabCase(str) {
        return StrReplace(this.ToSnakeCase(str), '_', '-')
    }

    static Truncate(str, maxLen, suffix := '...') {
        if StrLen(str) <= maxLen
            return str
        return SubStr(str, 1, maxLen - StrLen(suffix)) suffix
    }

    static Pad(str, len, char := ' ', side := 'right') {
        padding := ''
        loop len - StrLen(str)
            padding .= char

        return side = 'left' ? padding str : str padding
    }

    static Reverse(str) {
        result := ''
        loop parse str
            result := A_LoopField result
        return result
    }

    static WordCount(str) {
        return StrSplit(Trim(RegExReplace(str, '\s+', ' ')), ' ').Length
    }
}

