        this.recording := !this.recording

        if this.recording {
            this.actions := []
            this.lastTime := A_TickCount
            this.statusText.Value := 'Recording...'
            this.InstallHooks()
        } else {
            this.statusText.Value := 'Stopped (' this.actions.Length ' actions)'
            this.RemoveHooks()
        }
    }

    InstallHooks() {
        this.keyHook := InputHook('V')
        this.keyHook.KeyOpt('{All}', 'N')
        this.keyHook.OnKeyDown := this.OnKey.Bind(this)
        this.keyHook.Start()
    }

    RemoveHooks() {
        if this.keyHook
            this.keyHook.Stop()
    }

    OnKey(ih, vk, sc) {
        delay := A_TickCount - this.lastTime
        this.lastTime := A_TickCount

        this.actions.Push({type: 'key', vk: vk, sc: sc, delay: delay})
        this.countText.Value := 'Actions: ' this.actions.Length
    }

    Play() {
        if this.recording || this.playing || !this.actions.Length
            return

        this.playing := true
        this.statusText.Value := 'Playing...'

        for action in this.actions {
            Sleep(action.delay)
            if action.type = 'key'
                Send('{vk' Format('{:02X}', action.vk) '}')
        }

        this.playing := false
        this.statusText.Value := 'Done'
    }

    Clear() {
        this.actions := []
        this.countText.Value := 'Actions: 0'
        this.statusText.Value := 'Cleared'
    }
}

