class EventEmitter {
    __New() {
        this.listeners := Map()
    }

    Subscribe(event, callback) {
        if !this.listeners.Has(event)
            this.listeners[event] := []

        id := A_TickCount '_' Random(1000, 9999)
        this.listeners[event].Push({id: id, callback: callback})

        return id
    }

    Unsubscribe(event, id) {
        if !this.listeners.Has(event)
            return false

        for i, listener in this.listeners[event] {
            if listener.id = id {
                this.listeners[event].RemoveAt(i)
                return true
            }
        }
        return false
    }

    Publish(event, data := '') {
        if !this.listeners.Has(event)
            return 0

        count := 0
        for listener in this.listeners[event] {
            listener.callback.Call(data)
            count++
        }
        return count
    }

    Once(event, callback) {
        wrapper := ''
        wrapper := (data) => (
            callback.Call(data),
            this.Unsubscribe(event, id)
        )
        id := this.Subscribe(event, wrapper)
        return id
    }
}

