        return this.Request('GET', url)
    }

    Post(url, data) {
        body := (data is Map || data is Object) ? JSON.Dump(data) : data
        return this.Request('POST', url, body)
    }

    Put(url, data) {
        body := (data is Map || data is Object) ? JSON.Dump(data) : data
        return this.Request('PUT', url, body)
