class WebSocketClient {
    __New(url) {
        this.url := url
        this.connected := false
        this.onMessage := ''
        this.onClose := ''
        this.onError := ''

        this.ie := ComObject('InternetExplorer.Application')
        this.ie.Visible := false
        this.ie.Navigate('about:blank')

        while this.ie.Busy
            Sleep(10)
    }

    Connect() {
        script := '
        (
            window.ws = new WebSocket("' this.url '");
            window.messages = [];
            window.wsStatus = "connecting";

            ws.onopen = function() {
                window.wsStatus = "connected";
            };

            ws.onmessage = function(e) {
                window.messages.push(e.data);
            };

            ws.onclose = function() {
                window.wsStatus = "closed";
            };

            ws.onerror = function() {
                window.wsStatus = "error";
            };
        )'

        this.ie.Document.parentWindow.execScript(script)

        ; Wait for connection
        loop 50 {
            status := this.ie.Document.parentWindow.wsStatus
            if status = 'connected' {
                this.connected := true
                return true
            }
            if status = 'error'
                return false
            Sleep(100)
        }
        return false
    }

    Send(message) {
        if !this.connected
            return false
        this.ie.Document.parentWindow.ws.send(message)
        return true
    }

    Receive() {
        messages := this.ie.Document.parentWindow.messages
        result := []

        while messages.length > 0
            result.Push(messages.shift())

        return result
    }

    Close() {
        if this.connected
            this.ie.Document.parentWindow.ws.close()
        this.ie.Quit()
    }
}

; Usage
ws := WebSocketClient('wss://echo.websocket.org')
if ws.Connect() {
    ws.Send('Hello WebSocket!')
    Sleep(1000)
    messages := ws.Receive()
    for msg in messages
        MsgBox(msg)
    ws.Close()
}
