class TimerScheduler {
    static timers := Map()
    static counter := 0

    static SetTimeout(callback, delay) {
        id := ++this.counter

        wrapper := () => (
            SetTimer(this.timers[id].fn, 0),
            this.timers.Delete(id),
            callback.Call()
        )

        this.timers[id] := {fn: wrapper, type: 'timeout'}
        SetTimer(wrapper, -delay)

        return id
    }

    static SetInterval(callback, interval) {
        id := ++this.counter

        wrapper := () => callback.Call()

        this.timers[id] := {fn: wrapper, type: 'interval', interval: interval}
        SetTimer(wrapper, interval)

        return id
    }

    static Clear(id) {
        if this.timers.Has(id) {
            SetTimer(this.timers[id].fn, 0)
            this.timers.Delete(id)
            return true
        }
        return false
    }

    static ClearAll() {
        for id, timer in this.timers
            SetTimer(timer.fn, 0)
        this.timers.Clear()
    }

    static Pause(id) {
        if this.timers.Has(id) {
            SetTimer(this.timers[id].fn, 0)
            this.timers[id].paused := true
            return true
        }
        return false
    }

    static Resume(id) {
        if this.timers.Has(id) && this.timers[id].paused {
            interval := this.timers[id].type = 'interval' ? this.timers[id].interval : -1000
            SetTimer(this.timers[id].fn, interval)
            this.timers[id].paused := false
            return true
        }
        return false
    }
}

