class Logger {
    static levels := Map(
        'DEBUG', 10,
        'INFO', 20,
        'WARN', 30,
        'ERROR', 40
    )

    __New(logFile := '', minLevel := 'DEBUG') {
        this.logFile := logFile ? logFile : A_ScriptDir '\app.log'
        this.minLevel := minLevel
        this.console := true
        this.timestamps := true
    }

    Log(level, message, data := '') {
        if Logger.levels[level] < Logger.levels[this.minLevel]
            return

        timestamp := this.timestamps ? '[' FormatTime(A_Now, 'yyyy-MM-dd HH:mm:ss') '] ' : ''
        entry := timestamp '[' level '] ' message

        if data != ''
            entry .= ' | Data: ' String(data)

        if this.console
            OutputDebug(entry)

        if this.logFile {
            try FileAppend(entry '`n', this.logFile, 'UTF-8')
        }
    }

    Debug(message, data := '') {
        this.Log('DEBUG', message, data)
    }

    Info(message, data := '') {
        this.Log('INFO', message, data)
    }

    Warn(message, data := '') {
        this.Log('WARN', message, data)
    }

    Error(message, data := '') {
        this.Log('ERROR', message, data)
    }

    Clear() {
        if FileExist(this.logFile)
            FileDelete(this.logFile)
    }
}

